import React, { useEffect, useState } from 'react';
import Instance from '../Api/Unsplashapi';
import Navigation from '../navigation/Navigation';
import ImageGallary from './ImageGallary';
import ClipLoader from "react-spinners/ClipLoader";

function SearchBar() {

  const [images, setImages] = useState([])
  const [query, setQuery] = useState('')
  const [loading, setLoading] = useState(true)

  const handelSearch = () => (
    Instance.get(`search/photos?query=${query}`)
    .then(responce => {
      console.log(responce)
      setImages(responce.data.results)
      setLoading(false)
    })
  )

  return (
    <>
    <Navigation/>
    <div className="container">

            <div className="searchbox">
                <h2>Search Images</h2>
                <div className="search">

                <div className="searchField">

                {
      loading ?

      <ClipLoader color={"#F37A24"} loading={loading}  size={30}/>

      :
                    <input 
                    type="text" 
                    placeholder="Search Images here ..."
                    value={query}
                    onChange={e => setQuery(e.target.value)}
                    onKeyPress={e => e.key === 'Enter' && handelSearch()}
                    />    

                }
                </div>
            </div>
        </div>
        <ImageGallary gallaryImage={images}/>


        </div>
    </>

  )
}

export default SearchBar;
