import React, { useState } from 'react';
import {Instance, imageApi } from '../lib/Unsplashapi';
import Navigation from '../Component/navigation/Navigation';
import ImageGallary from '../Component/ImageGallary';
import Loading from '../Component/Loading/Loading';
function SearchBar() {

  const [images, setImages] = useState([])
  const [query, setQuery] = useState('')
  const [isLoading, setIsLoading] = useState(false)
  const [errorMessage, setErrorMessage] = useState('')


  const handelSearch = () => {
    setIsLoading(true);
    const data = imageApi(query)
    .then(responce => {
      console.log(responce)
      setImages(responce.data.results)

    })
    .catch(() => {
      setErrorMessage("Unable to fetch Images");
    })
    .finally(() => {
      setIsLoading(false)
    })
  }
  return (
    <>
    <Navigation/>
      <div className="container">
      <div className="search-box">
          <h2>Search Images</h2>
          <div className="search">
          <div className="search-Field">
              <input 
              type="text" 
              placeholder="Search Images here ..."
              value={query}
              onChange={e => setQuery(e.target.value)}
              onKeyPress={e => e.key === 'Enter' && handelSearch()}
              />   
          </div>
          </div>
      </div>
        {
          isLoading ? <Loading/> : <ImageGallary gallaryImage={images}/>
        }
        {
          errorMessage && <div className='error'>{errorMessage}</div>
        }
      </div>
    </>

  )
}

export default SearchBar;
