import React, { useState } from 'react'
import Instance from '../Api/Unsplashapi'
import Navigation from '../navigation/Navigation'
import UsersDisplay from './UsersDisplay'

function SearchUser() {

    const [query, setQuery] = useState('')
    const [userData, setUserData] = useState([])
    const [pages, setPages] = useState(2)
    const [isLoading, setIsLoading] = useState(false)
    const [errorMessage, setErrorMessage] = useState('')

    const apiCall = (page_count) => {
        setIsLoading(true)
        const data = Instance.get(`search/users?query=${query}&page=${page_count}`)
        .then((res) => {
            console.log(res)
            return res.data.results
        })
        .catch(() => {
            setErrorMessage("Unable to fetch user")
        })
        .finally(()=>{
            setIsLoading(false)
        })
        return data;
    }

    const handleSearch = async (page_count = 1) => {
        console.log(page_count)
        const result = await apiCall(page_count)
        setUserData(result)
    }

    const pageIncrement = async () => {
        setPages((prev) => {
          return  prev + 1
        })
        const newData = await apiCall(pages)
        setUserData([...userData, ...newData])
    }

    return (
        <>
        <Navigation/>
        <div className="container">
            <div className="searchbox">
                <h2>Search Users</h2>
                <div className="search">
                    <div className="searchField">
                        <input 
                        type="text" 
                        name='user'
                        placeholder="Search User here ..."
                        value={query}
                        onChange={e => setQuery(e.target.value)}
                        onKeyPress={e => e.key === 'Enter' && handleSearch()}
                        />    
                    </div>
                </div>
                {/* <div>
                    <button type="text" onClick={()=> {pageIncrement()}}>Load More</button>
                </div> */}
            </div>
            <UsersDisplay users={userData} load={pageIncrement}/>
        </div>
        </>
    )
}

export default SearchUser
