import axios from "axios";

const Instance = axios.create({
    baseURL: 'https://api.unsplash.com/',
    headers: {
        Authorization: 'Client-ID o2dtp54Wcz-CyilYtblOYEhegiTHoc9HZg4-FAHQits'
    }
})


const userApi = (query, page_count) => {
    const userData = Instance.get(`search/users?query=${query}&page=${page_count}`)
    .then((res) => {
        console.log(res)
        return res.data.results
    })
    .catch(() => {
          return ("Unable to fetch user")
    })
    return userData;
}

const imageApi = (query) => {
    const imageData = Instance.get(`search/photos?query=${query}`)
    .then((res) => {
        console.log(res)
        return res.data.results
    })
    .catch(() => {
          return ("Unable to fetch Image")
    })
    return imageData;
}

export {Instance , userApi, imageApi}
