import './App.css';
import './Component/CSS/NormalizeCss.css'
import DashBoard from './Component/DashBoard';
import ClipLoader from "react-spinners/ClipLoader";
import React, {useState, useEffect} from 'react';
import {
  BrowserRouter,
  Routes,
  Route,
} from "react-router-dom";

import SearchBar from './Component/Images/SearchImages';
import SearchUser from './Component/Users/SearchUser';
import UserProfile from './Component/Users/UserProfile';
import Loading from './Component/Loading/Loading';

function App() {

  const [loading, setLoading] = useState(false)

  useEffect(()=>{
      setLoading(true)
      // setTimeout(()=>{
      //   setLoading(false);
      // }, 8000);
  }, [])

  return (
    <div className="App"> 
    {
      loading ?

      <ClipLoader color={"#F37A24"} loading={loading}  size={30}/>

      :

      <BrowserRouter>
      <Routes>
        <Route path='/' element={<DashBoard/>}></Route>
        <Route path='searchimage' element={<SearchBar/>}></Route>
        <Route path='searchuser' element={<SearchUser/>}></Route>
        <Route path='userprofile' element={<UserProfile/>}></Route>
        <Route path='userprofile/:username' element={<UserProfile/>}></Route>
      </Routes>
      </BrowserRouter>

    }

    </div>
  );
}

export default App;
